import sys

# Initialize game board
def init_board():
    return [[0, 0, 0] for _ in range(3)]

# Print the current state of the board
def print_board(board):
    for row in board:
        print(' '.join(str(cell) for cell in row))

# Check for win or draw
def check_win(board):
    # Check rows, columns, and diagonals for a win
    for i in range(3):
        if board[i][0] == board[i][1] == board[i][2] != 0:
            return board[i][0]
        if board[0][i] == board[1][i] == board[2][i] != 0:
            return board[0][i]
    if board[0][0] == board[1][1] == board[2][2] != 0 or board[0][2] == board[1][1] == board[2][0] != 0:
        return board[1][1]
    # Check for draw
    if all(cell != 0 for row in board for cell in row):
        return 'Draw'
    return None

# Main game function
def main():
    board = init_board()
    current_player = 1
    while True:
        print_board(board)
        try:
            x, y = map(int, input(f'Player {current_player}, enter your move (row,col): ').strip().split(','))
            if board[x][y] != 0:
                print('This position is already taken.')
                continue
            board[x][y] = current_player
        except (ValueError, IndexError):
            print('Invalid input. Please enter your move as two digits separated by a comma, e.g., 0,1.')
            continue
        winner = check_win(board)
        if winner:
            if winner == 'Draw':
                print('Draw')
            else:
                print(f'Player {winner} won!')
            break
        current_player = 2 if current_player == 1 else 1

if __name__ == '__main__':
    sys.exit(main())
