import sys

def print_board(board):
    for row in board:
        print('|'.join(row))
        print('-'*5)

def get_winner(board):
    # Check horizontal, vertical, and diagonal lines
    for i in range(3):
        if len(set(board[i])) == 1 and board[i][0] != ' ':
            return board[i][0]
        if len(set([row[i] for row in board])) == 1 and board[0][i] != ' ':
            return board[0][i]
