    x, y = map(int, move.split(','))
    return 0 <= x < 3 and 0 <= y < 3 and board[x][y] == ' '

def main():
    board = [[' ' for _ in range(3)] for _ in range(3)]
    current_player = '1'
    while True:
        print_board(board)
        move = input(f'Player {current_player}, enter your move as x,y: ')
