# URL Shortener CLI Tool
import hashlib

# Dictionary to store the mapping of original to shortened URLs
url_mapping = {}

def shorten_url(url):
    """Shortens a given URL."""
    # Simple hash-based shortening
    hash_object = hashlib.sha256(url.encode())
    short_hash = hash_object.hexdigest()[:10]  # Using first 10 characters for simplicity
    short_url = f'http://short.url/{short_hash}'
    url_mapping[short_url] = url
    return short_url

def retrieve_url(shortened_url):
    """Retrieves the original URL from a shortened URL."""
    if shortened_url in url_mapping:
        return url_mapping[shortened_url]
    else:
        return 'URL not found'

if __name__ == '__main__':
    while True:
        user_input = input('Enter a URL or a shortened URL to retrieve the original: ')
        if user_input.startswith('http://short.url/'):
            # This is a shortened URL
            original_url = retrieve_url(user_input)
            print(original_url)
        else:
            # This is a long URL that needs to be shortened
            shortened_url = shorten_url(user_input)
            print(shortened_url)
        print('----------------')

