def main():
    import sys
    def print_board(board):
        for row in board:
            print(' '.join(str(x) for x in row))
    def check_win(board, player):
        for row in board:
            if all(x == player for x in row):
                return True
        for col in range(3):
            if all(board[row][col] == player for row in range(3)):
                return True
        if all(board[i][i] == player for i in range(3)) or all(board[i][2-i] == player for i in range(3)):
            return True
        return False
    def check_draw(board):
        return all(board[row][col] != 0 for row in range(3) for col in range(3))
    board = [[0] * 3 for _ in range(3)]
    current_player = 1
    while True:
        try:
            x, y = map(int, input('Enter row and column for player ' + str(current_player) + ' (comma-separated): ').split(','))
            if board[x][y] != 0:
                print('Invalid move, the cell is already filled.')
                continue
        except ValueError: 
            print('Invalid input. Please enter two numbers separated by a comma.')
            continue
        board[x][y] = current_player
        print_board(board)
        if check_win(board, current_player):
            print(f'Player {current_player} won!')
            break
        if check_draw(board):
            print('Draw')
            break
        current_player = 2 if current_player == 1 else 1
if __name__ == '__main__':
    main()

