# Tic-Tac-Toe Game in Python CLI

def main():
    board = [[0, 0, 0] for _ in range(3)]
    player_turn = 1

    def check_win(player):
        # Check rows, columns, and diagonals for a win
        for i in range(3):
            if all(board[i][j] == player for j in range(3)) or all(board[j][i] == player for j in range(3)):
                return True
        if board[0][0] == board[1][1] == board[2][2] == player or board[0][2] == board[1][1] == board[2][0] == player:
            return True
        return False

    while True: # Game loop
        print(f'Current board:')
        for row in board:
            print(' '.join(str(x) for x in row))
        move = input(f'Player {player_turn} enter your move (x,y): ').strip()
        if not move: # Handle no input gracefully
            continue

        try:
            x, y = map(int, move.split(','))
            if board[x][y] != 0:
                print(f'Space {x},{y} is already taken.')
                continue
            if not (0 <= x < 3 and 0 <= y < 3):
                print('Coordinates out of bounds. Please use coordinates within the grid.')
                continue
        except ValueError:
            print('Invalid input. Please use x,y coordinates in the correct format.')
            continue

        board[x][y] = player_turn
        if check_win(player_turn):
            print(f'Player {player_turn} won!')
            break
        if all(all(cell != 0 for cell in row) for row in board):
            print('Draw')
            break
        player_turn = 3 - player_turn

main()

